import React, { useState } from 'react';
import './Tooltip.css';

function Tooltip(props) {
  const [visible, setVisible] = useState(true);

  const {
    content,
    position = "top", // Default is top
    style = {}
  } = props;

  const contentStyle = {
    ...style,
    ...position && { [position]: 'auto' }
  };

  return (
    // Remove the unnecessary div wrappers, as we'll create them in App.js
    <div
      className={`tooltip-outline tooltip-content ${position}`} // Combine classes
      style={contentStyle}
    >
      {props.children} {/* Display the button content passed from App.js */}
      {content}
    </div>
  );
}

export default Tooltip;
